import { FaTimes } from 'react-icons/fa'

const Task = ({ task, onDelete, onToggle }) => {
  return (
    <div className={`task ${task.reminder ? 'reminder' : ''}`} onDoubleClick={() => onToggle(task._ID)}>
      <h3>
        {task.text} 
        <FaTimes 
          style={{color: 'red'}} 
          onClick={() => onDelete(task._ID)}
        />
      </h3>
      {/* <p>{task.day}</p> */}
    </div>
  )
}

export default Task
